"use strict";(self.webpackChunknext_info=self.webpackChunknext_info||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Introduction","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Web Search is Primitive","href":"/docs/introduction/web-search-is-primitive","docId":"introduction/web-search-is-primitive"},{"type":"link","label":"Request for Comments","href":"/docs/introduction/request-for-comments","docId":"introduction/request-for-comments"},{"type":"link","label":"Defining Terms","href":"/docs/introduction/defining-terms","docId":"introduction/defining-terms"}]},{"type":"category","label":"What\'s Wrong with Web Search?","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Poor Quality of Search Results","href":"/docs/whats-wrong-with-web-search/poor-quality-of-search-results","docId":"whats-wrong-with-web-search/poor-quality-of-search-results"},{"type":"link","label":"Commercial Dominance of the Web","href":"/docs/whats-wrong-with-web-search/commercial-dominance-of-the-web","docId":"whats-wrong-with-web-search/commercial-dominance-of-the-web"},{"type":"link","label":"Suppression of Better Content","href":"/docs/whats-wrong-with-web-search/suppression-of-new-and-better-content","docId":"whats-wrong-with-web-search/suppression-of-new-and-better-content"},{"type":"link","label":"Poor Searcher Productivity","href":"/docs/whats-wrong-with-web-search/poor-searcher-productivity","docId":"whats-wrong-with-web-search/poor-searcher-productivity"},{"type":"link","label":"Lack of Competition","href":"/docs/whats-wrong-with-web-search/lack-of-competition","docId":"whats-wrong-with-web-search/lack-of-competition"},{"type":"link","label":"Lack of Privacy","href":"/docs/whats-wrong-with-web-search/lack-of-privacy","docId":"whats-wrong-with-web-search/lack-of-privacy"}]},{"type":"category","label":"How We Are Open","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Radically Open","href":"/docs/how-we-are-open/radically-open","docId":"how-we-are-open/radically-open"},{"type":"link","label":"Open Data","href":"/docs/how-we-are-open/radically-open-data","docId":"how-we-are-open/radically-open-data"},{"type":"link","label":"Open Algorithms","href":"/docs/how-we-are-open/radically-open-algorithms","docId":"how-we-are-open/radically-open-algorithms"},{"type":"link","label":"Open Finances","href":"/docs/how-we-are-open/radically-open-finances","docId":"how-we-are-open/radically-open-finances"}]},{"type":"category","label":"Your Ownership and Privacy","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Radical Ownership","href":"/docs/your-ownership-and-privacy/radical-ownership","docId":"your-ownership-and-privacy/radical-ownership"},{"type":"link","label":"Private by Default","href":"/docs/your-ownership-and-privacy/private-by-default","docId":"your-ownership-and-privacy/private-by-default"},{"type":"link","label":"Your Data","href":"/docs/your-ownership-and-privacy/your-data","docId":"your-ownership-and-privacy/your-data"}]},{"type":"category","label":"How We Make Search Better","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Fixing Stale Results","href":"/docs/how-we-make-search-better/fixing-stale-results","docId":"how-we-make-search-better/fixing-stale-results"},{"type":"link","label":"Local First","href":"/docs/how-we-make-search-better/local-first","docId":"how-we-make-search-better/local-first"},{"type":"link","label":"Advanced Features","href":"/docs/how-we-make-search-better/advanced-features","docId":"how-we-make-search-better/advanced-features"},{"type":"link","label":"The Trust Network","href":"/docs/how-we-make-search-better/the-trust-network","docId":"how-we-make-search-better/the-trust-network"},{"type":"link","label":"How We Monetize","href":"/docs/how-we-make-search-better/how-we-monetize","docId":"how-we-make-search-better/how-we-monetize"}]},{"type":"category","label":"Appendixes","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Overview","href":"/docs/appendixes/overview","docId":"appendixes/overview"},{"type":"link","label":"Making a Better World","href":"/docs/appendixes/making-a-better-world","docId":"appendixes/making-a-better-world"},{"type":"link","label":"Outside Initial Scope","href":"/docs/appendixes/outside-initial-scope","docId":"appendixes/outside-initial-scope"},{"type":"link","label":"The Current Search Landscape","href":"/docs/appendixes/current-search-landscape","docId":"appendixes/current-search-landscape"}]},{"type":"link","label":"Front Matter","href":"/docs/","docId":"README"}]},"docs":{"appendixes/current-search-landscape":{"id":"appendixes/current-search-landscape","title":"The Current Search Landscape","description":"Introduction","sidebar":"tutorialSidebar"},"appendixes/making-a-better-world":{"id":"appendixes/making-a-better-world","title":"Making a Better World","description":"We are eager to be part of bringing life and hope, justice and equality, in ever greater measure to this world. A search engine may seem far divorced from humanitarian endeavor but we believe that a search engine can play an important part in improving the world we all live in \\\\(consequently, it can also have a detrimental effect on the world\\\\).","sidebar":"tutorialSidebar"},"appendixes/outside-initial-scope":{"id":"appendixes/outside-initial-scope","title":"Outside Initial Scope","description":"In the body of our proposal we have limited ourselves to a select few distinguishing features because:","sidebar":"tutorialSidebar"},"appendixes/overview":{"id":"appendixes/overview","title":"Overview","description":"The following appendixes are useful but not necessary reading for those seeking to understand the next generation web search proposal. We have sought to keep the substance of the proposal limited in extent but hope some will find these additional sections useful.","sidebar":"tutorialSidebar"},"how-we-are-open/radically-open":{"id":"how-we-are-open/radically-open","title":"Radically Open","description":"We love open source. To be a consumer of a product/service with no leverage to encourage the producer to improve their offering can leave us feeling helpless and have a real negative effect upon quality of life. Open source provides us with leverage.","sidebar":"tutorialSidebar"},"how-we-are-open/radically-open-algorithms":{"id":"how-we-are-open/radically-open-algorithms","title":"Open Algorithms","description":"Search Engines utilize complex algorithms to rank the search results that are displayed to consumers. Generally the intricacies of these algorithms are hidden from consumers for a variety of reasons. We will not be taking a closed approach and instead will make our algorithms available and open source.","sidebar":"tutorialSidebar"},"how-we-are-open/radically-open-data":{"id":"how-we-are-open/radically-open-data","title":"Open Data","description":"We will make the data we crawl and utilize for the search engine freely available.","sidebar":"tutorialSidebar"},"how-we-are-open/radically-open-finances":{"id":"how-we-are-open/radically-open-finances","title":"Open Finances","description":"We want to be transparent about our finances and how we are utilizing them. We don\'t have anything to be ashamed of so we will be making available detailed reports of our income and expenditures on at least a quarterly basis.","sidebar":"tutorialSidebar"},"how-we-make-search-better/advanced-features":{"id":"how-we-make-search-better/advanced-features","title":"Advanced Features","description":"We believe in simple, intuitive user interfaces. When someone visits Phoebe they should be able to utilize it productively instantly. This is the \\"Apple\\" way.","sidebar":"tutorialSidebar"},"how-we-make-search-better/fixing-stale-results":{"id":"how-we-make-search-better/fixing-stale-results","title":"Fixing Stale Results","description":"Current Search Engines are Good for Real-Time \\\\(\\"Time-Bound\\"\\\\) Results","sidebar":"tutorialSidebar"},"how-we-make-search-better/how-we-monetize":{"id":"how-we-make-search-better/how-we-monetize","title":"How We Monetize","description":"Phoebe will cost money to implement, maintain, and scale. How will Phoebe monetize itself in a sustainable way? At the moment we have three primary methods in mind:","sidebar":"tutorialSidebar"},"how-we-make-search-better/local-first":{"id":"how-we-make-search-better/local-first","title":"Local First","description":"We will offer browser extensions which individuals can optionally install. These extensions will retain \\\\(if desired\\\\) local query data and also sync data down from our servers that can expedite queries.","sidebar":"tutorialSidebar"},"how-we-make-search-better/the-trust-network":{"id":"how-we-make-search-better/the-trust-network","title":"The Trust Network","description":"Introduction","sidebar":"tutorialSidebar"},"introduction/defining-terms":{"id":"introduction/defining-terms","title":"Defining Terms","description":"Words can get unwieldy quickly. Here are a few words/phrases we are using in specific ways throughout our proposal.","sidebar":"tutorialSidebar"},"introduction/request-for-comments":{"id":"introduction/request-for-comments","title":"Request for Comments","description":"This document can be considered a Request for Comments \\\\(RFC\\\\). We believe we have a way forward but are eager to receive feedback from you.","sidebar":"tutorialSidebar"},"introduction/web-search-is-primitive":{"id":"introduction/web-search-is-primitive","title":"Web Search is Primitive","description":"Web Search has improved significantly over the past several decades. However, web search is still distressingly inadequate for the needs of humanity. This document outlines our proposal for a next generation web search engine \\\\(provisionally \\"Phoebe\\"\\\\) that will significantly reduce the current chasm between what humans need from search and what search provides now.","sidebar":"tutorialSidebar"},"README":{"id":"README","title":"Front Matter","description":"This document outlines a proposal for a next generation web search engine which we believe can deliver results significantly better than any current search engine.","sidebar":"tutorialSidebar"},"whats-wrong-with-web-search/commercial-dominance-of-the-web":{"id":"whats-wrong-with-web-search/commercial-dominance-of-the-web","title":"Commercial Dominance of the Web","description":"\\"...the search engines have the power to grant fortunes and to take them away - it is as if they are the builder of roads, stoplights, and front doors to every business online.\\" - Alexander Halavais, Search Engine Society, 2017.","sidebar":"tutorialSidebar"},"whats-wrong-with-web-search/lack-of-competition":{"id":"whats-wrong-with-web-search/lack-of-competition","title":"Lack of Competition","description":"Google owns the vast majority of the web search market. When a single company dominates any market it often results in:","sidebar":"tutorialSidebar"},"whats-wrong-with-web-search/lack-of-privacy":{"id":"whats-wrong-with-web-search/lack-of-privacy","title":"Lack of Privacy","description":"The amount of data being retained by technology companies about us has been a growing concern. What data is being collected? How is it being used? Is it being shared or sold to other parties?","sidebar":"tutorialSidebar"},"whats-wrong-with-web-search/poor-quality-of-search-results":{"id":"whats-wrong-with-web-search/poor-quality-of-search-results","title":"Poor Quality of Search Results","description":"The results returned by search engines have improved significantly over prior engines BUT are still abysmal in so many cases. Disagree? Here are a few simple queries you can make to see the poor quality of current search results:","sidebar":"tutorialSidebar"},"whats-wrong-with-web-search/poor-searcher-productivity":{"id":"whats-wrong-with-web-search/poor-searcher-productivity","title":"Poor Searcher Productivity","description":"Search engines lack a number of simple and intuitive tools that could significantly improve a searcher\'s productivity. Some examples include:","sidebar":"tutorialSidebar"},"whats-wrong-with-web-search/suppression-of-new-and-better-content":{"id":"whats-wrong-with-web-search/suppression-of-new-and-better-content","title":"Suppression of Better Content","description":"Search engines do not intentionally suppress new and better sites/content \\\\(to the best of our knowledge\\\\) but it is the inevitable result of the algorithms currently used to calculate search result rankings.","sidebar":"tutorialSidebar"},"your-ownership-and-privacy/private-by-default":{"id":"your-ownership-and-privacy/private-by-default","title":"Private by Default","description":"A large part of Phoebe\'s uniqueness is the way it can utilize feedback from searchers to improve itself. As we mentioned in our section on data we don\'t want this advantage to come at the cost of your privacy - so we will be private by default.","sidebar":"tutorialSidebar"},"your-ownership-and-privacy/radical-ownership":{"id":"your-ownership-and-privacy/radical-ownership","title":"Radical Ownership","description":"Some of us are huge privacy buffs while others don\'t mind if data is collected as long as it improves our experience. Right now each person is stuck in one camp or the other, there is no middle ground. Read the next several pages to see how we intend to offer a middle ground that is safe and productive for all!","sidebar":"tutorialSidebar"},"your-ownership-and-privacy/your-data":{"id":"your-ownership-and-privacy/your-data","title":"Your Data","description":"You will be able to choose if and where your data is stored.","sidebar":"tutorialSidebar"}}}')}}]);